import React, { useEffect, useState } from 'react';
import AWS from '../aws-config';

const Education = () => {
  const [education, setEducation] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const dynamoDb = new AWS.DynamoDB.DocumentClient();

      const params = {
        TableName: 'Education',
        Key: {
          School: 'New Jersey Institute of Technology',
        },
      };

      try {
        const data = await dynamoDb.get(params).promise();
        if (data.Item) {
          setEducation(data.Item);
        } else {
          console.error('No education data found.');
        }
      } catch (error) {
        console.error('Error fetching education data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="education-container">
      <h1>Education</h1>
      {education ? (
        <div>
          {/* Display attributes in "Attribute: Value" format */}
          <div className="education-details">
            <p><strong>School:</strong> {education.School.S}</p>
            <p><strong>Major:</strong> {education.Major.S}</p>
            <p><strong>Concentration:</strong> {education.Concentration.S}</p>
            <p><strong>Minor:</strong> {education.Minor.S}</p>
            <p><strong>Dates:</strong> {education.Dates.S}</p>
          </div>

          {/* Degree and Highlights side by side */}
          <div className="degree-highlights">
            <div className="degree">
              <h2>Degree</h2>
              <a href={education.Degree.S} target="_blank" rel="noopener noreferrer">
                View Degree
              </a>
            </div>
            <div className="highlights">
              <h2>Highlights</h2>
              <p>{education.Highlights.S}</p>
            </div>
          </div>
        </div>
      ) : (
        <p>No education data found.</p>
      )}
    </div>
  );
};

export default Education;
