import React, { useEffect, useState } from 'react';
import AWS from '../aws-config';

const Education = () => {
  const [education, setEducation] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const dynamoDb = new AWS.DynamoDB.DocumentClient();

      const params = {
        TableName: 'Education',
        Key: {
          School: 'New Jersey Institute of Technology',
        },
      };

      console.log('Fetching data with params:', params); // Debugging

            try {
                const data = await dynamoDb.get(params).promise();
                console.log('Data fetched:', data); // Debugging

                if (data.Item) {
                    console.log('Item found:', data.Item); // Debugging
                    setEducation(data.Item);
                } else {
                    console.error('No data found for the given key.'); // Debugging
                }
            } catch (error) {
                console.error('Error fetching education data:', error); // Debugging
            }
        };

    fetchData();
  }, []);

  return (
    <div className="education-container">
        <h1>{education.School.S}</h1>
      {education ? (
        <div>

          {/* Subtitle: Major, Concentration, Minor */}
          <h2>
            {education.Major.S}, with a Concentration in {education.Concentration.S}, and a {education.Minor.S} Minor
          </h2>

          {/* Sub-subheading: Dates */}
          <h3>{education.Dates.S}</h3>

          {/* Degree Image (Centered) */}
          <div className="degree-image">
            <a href={education.Degree.S} target="_blank" rel="noopener noreferrer">
              <img src={education.Degree.S} alt="Degree" />
            </a>
          </div>

          {/* Highlights (Centered) */}
          <div className="highlights">
            <p>{education.Highlights.S}</p>
          </div>
        </div>
      ) : (
        <p>No education data found.</p>
      )}
    </div>
  );
};

export default Education;
