import React, { useEffect, useState } from 'react';
import AWS from '../aws-config';
import { Link } from 'react-router-dom';

const Main = () => {
    const [mainData, setMainData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            const dynamoDb = new AWS.DynamoDB.DocumentClient();

            const params = {
                TableName: 'Main',
                Key: {
                    MainPage: 'No Tabs',
                },
            };

            console.log('Fetching data with params:', params); // Debugging

            try {
                const data = await dynamoDb.get(params).promise();
                console.log('Data fetched:', data); // Debugging

                if (data.Item) {
                    console.log('Item found:', data.Item); // Debugging
                    setMainData(data.Item);
                } else {
                    console.error('No data found for the given key.'); // Debugging
                }
            } catch (error) {
                console.error('Error fetching main data:', error); // Debugging
            }
        };

        fetchData();
    }, []);

    return (
        <div className="main-container">
            <h1>Main</h1> {/* Centered title at the top */}
            {mainData ? (
                <div className="content">
                    {/* Image on the left */}
                    <img src={mainData.ComputerEngineerPicture} alt="Computer Engineer" />

                    {/* Purpose and Paragraph on the right */}
                    <div className="bio">
                        <h2>Purpose</h2> {/* Purpose subheading */}
                        <p>{mainData.Purpose}</p>

                        {/* Add the oval button for the About Nour Website Project */}
                        <a href="/projects/about-nour" className="oval-button">About Nour Website Project</a>
                    </div>
                </div>
            ) : (
                <p>No main data found.</p>
            )}
        </div>
    );
};

export default Main;
