import React, { useEffect, useState } from 'react';
import AWS from '../aws-config';
import { Link } from 'react-router-dom';

const NetworkInternship = ({ section }) => {
  const [internship, setInternship] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const dynamoDb = new AWS.DynamoDB.DocumentClient();

      const params = {
        TableName: 'Internships',
        Key: {
          InternshipCompany: 'Cloud Vision Technology, LLC',
          InternshipName: 'Network Engineering Internship',
        },
      };

      try {
        const data = await dynamoDb.get(params).promise();
        if (data.Item) {
          setInternship(data.Item);
        } else {
          console.error('No internship data found.');
        }
      } catch (error) {
        console.error('Error fetching internship:', error);
      }
    };

    fetchData();
  }, [section]);

  return (
    <div className="internship-container">
      <h1>Network Engineering Internship</h1>
      {internship && internship.Subheading && internship.Subheading.M && (
        <div className="subheading">
          <p>
            <strong>{internship.Subheading.M.Title?.S}</strong> â€” {internship.Subheading.M.Dates?.S} in {internship.Subheading.M.Location?.S}
          </p>
        </div>
      )}
      {internship ? (
        <div>
          {section === 'role' && (
            <div className="role-overview">
              <h2>Role Overview</h2>
              {internship.RoleAndResponsibilities?.RoleOverview ? (
                <p>{internship.RoleAndResponsibilities.RoleOverview}</p>
              ) : (
                <p>No role overview data found.</p>
              )}

              <h2>Responsibilities</h2>
              {internship.RoleAndResponsibilities?.Responsibilities ? (
                <p dangerouslySetInnerHTML={{ __html: internship.RoleAndResponsibilities.Responsibilities }} />
              ) : (
                <p>No responsibilities data found.</p>
              )}
            </div>
          )}
          {section === 'accomplishments' && (
            <div className="accomplishments">
              <h2>Accomplishments</h2>
              <p dangerouslySetInnerHTML={{ __html: internship.Accomplishments }} />
            </div>
          )}
          {section === 'project' && (
            <div className="project">
              <h2>Project</h2>
              <p>{internship.Project}</p>
              <a href="/projects/networking" className="oval-button">Networking Projects</a>
            </div>
          )}
        </div>
      ) : (
        <p>No internship data found.</p>
      )}
    </div>
  );
};

export default NetworkInternship;
