import React, { useEffect, useState } from 'react';
import AWS from '../aws-config';
import { Link } from 'react-router-dom';
const Internship2023 = ({ section }) => {
    const [internship, setInternship] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            const dynamoDb = new AWS.DynamoDB.DocumentClient();

            const params = {
                TableName: 'Internships',
                Key: {
                    InternshipCompany: 'Amazon Web Services, Inc.',
                    InternshipName: 'AWS Summer 2023 Internship',
                },
            };

            console.log('Fetching data with params:', params); // Debugging

            try {
                const data = await dynamoDb.get(params).promise();
                console.log('Data fetched:', data); // Debugging
                setInternship(data.Item);
            } catch (error) {
                console.error('Error fetching internship:', error);
            }
        };

        fetchData();
    }, [section]);

    // Function to render HTML content
    const renderHTML = (html) => {
        return { __html: html };
    };

    return (
        <div className="internship-container">
            <h1>Amazon Summer 2023 Internship</h1>
            {internship ? (
                <div>
                    {section === 'role' && (
                        <div className="role-overview">
                            {/* Role Overview Section */}
                            <h2>Role Overview</h2>
                            {internship.RoleAndResponsibilities?.RoleOverview ? (
                                <p>{internship.RoleAndResponsibilities.RoleOverview}</p>
                            ) : (
                                <p>No role overview data found.</p>
                            )}

                            {/* Responsibilities Section */}
                            <h2>Responsibilities</h2>
                            {internship.RoleAndResponsibilities?.Responsibilities ? (
                                <p dangerouslySetInnerHTML={renderHTML(internship.RoleAndResponsibilities.Responsibilities)} />
                            ) : (
                                <p>No responsibilities data found.</p>
                            )}
                        </div>
                    )}
                    {section === 'accomplishments' && (
                        <div className="accomplishments">
                            <h2>Accomplishments</h2>
                            <p dangerouslySetInnerHTML={renderHTML(internship.Accomplishments)} />
                        </div>
                    )}
                    {section === 'project' && (
                        <div className="project">
                            <h2>Project</h2>
                            <p>{internship.Project}</p>
                            <a href="/projects/apollo" className="oval-button">Apollo Project</a>
                        </div>
                    )}
                </div>
            ) : (
                <p>No internship data found.</p>
            )}
        </div>
    );
};

export default Internship2023;
